package demo;

public class variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      float b=6.9f;
      float a=4.8f;
      float c=10.89f;
      double d=20.89d;
     float f=a/b;
      double e=d+f;
      float h=c-b;
      double i=c*d;
      int j=(int)i;
      System.out.println(f);
      System.out.println(e);
      System.out.println(h);
      System.out.println(j);//implicit casting
	}

}
